// Determinar qu√© tipo de correo enviar seg√∫n d√≠as transcurridos
const items = $input.all();
const output = [];

for (const item of items) {
  const dias = parseInt(item.json.dias_desde_creacion);
  const ultimoCorreo = item.json.ultimo_correo_enviado;

  let tipoCorreo = null;

  // D√≠a 2: Recordatorio (solo si ya se envi√≥ solicitud inicial)
  if (dias >= 2 && dias < 4 && ultimoCorreo === 'SOLICITUD_INICIAL') {
    tipoCorreo = 'RECORDATORIO_DIA2';
  }
  // D√≠a 4: Ultim√°tum (solo si ya se envi√≥ recordatorio d√≠a 2)
  else if (dias >= 4 && ultimoCorreo === 'RECORDATORIO_DIA2') {
    tipoCorreo = 'ULTIMATUM_DIA4';
  }

  if (tipoCorreo) {
    output.push({
      json: {
        ...item.json,
        tipo_correo_enviar: tipoCorreo
      }
    });
  }
}

return output;

